    private void btnSumarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSumarActionPerformed
    String strNumero1 = txtPrimerNumero.getText();
    String strNumero2 = txtSegundoNumero.getText();
    
    if (strNumero1.isEmpty() || strNumero2.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Por favor ingresa dos números.", 
                "MENSAJE",JOptionPane.WARNING_MESSAGE);
        return;
    }     
    EnteroMuyGrande numero1 = new EnteroMuyGrande(strNumero1);
    EnteroMuyGrande numero2 = new EnteroMuyGrande(strNumero2);
    
    EnteroMuyGrande resultado = numero1.suma(numero2);
    
    txtResultado.setText(resultado.aString());
    }//GEN-LAST:event_btnSumarActionPerformed

