    private void btnMulSimActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMulSimActionPerformed
    // Obtener los números ingresados como cadenas desde los campos de texto
    String strNumero1 = txtPrimerNumero.getText();
    String strNumero2 = txtSegundoNumero.getText();

    // Verificar si se han ingresado números
    if (strNumero1.isEmpty() || strNumero2.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Por favor ingresa dos números.", 
                "MENSAJE",JOptionPane.WARNING_MESSAGE);
        return;
    }


        // Convertir las cadenas a BigIntegers y realizar la multiplicación
        EnteroMuyGrande numero1 = new EnteroMuyGrande(strNumero1);
        EnteroMuyGrande numero2 = new EnteroMuyGrande(strNumero2);
        EnteroMuyGrande resultado = numero1.multiplicacionSimplista(numero2);

        // Mostrar el resultado en el campo de resultado
        txtResultado.setText(resultado.aString());

    

    }//GEN-LAST:event_btnMulSimActionPerformed

