    private void btnInvertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInvertirActionPerformed
        String strNumero1 = txtPrimerNumero.getText();

        // Verificar si se ha ingresado un número
        if (strNumero1.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Por favor ingresa un número en el primer campo.", 
                "MENSAJE", JOptionPane.WARNING_MESSAGE);
            return;
        }

        // Convertir la cadena a EnteroMuyGrande y llamar al método para invertir la lista
        EnteroMuyGrande numero1 = new EnteroMuyGrande(strNumero1);
        numero1.invertir();

        // Mostrar el número invertido en el campo txtPrimerNumero
        txtResultado.setText(numero1.aString());  
    }//GEN-LAST:event_btnInvertirActionPerformed

